mapper.select:

select 
	count(*)
from 
	employee_attendances A


	   		and to_char(A.working_date, 'yyyy-MM')  = #{month}


	   		and A.employee_no = #{empNo}

select * from ( select row_number() over (order by A.working_date desc) as row_numbers, E.employee_no as employeeNo, E.employee_name as employeeName, P.position_name as positionName, D.dept_name as deptName, A.attendances_type as attendancesType, A.end_work_time as endWorkTime, A.working_date as workingDate from employees E, positions P, departments D, employee_attendances A where e.dept_no = d.dept_no and e.position_no = p.position_no and e.employee_no = A.employee_no
and to_char(A.working_date, 'yyyy-MM') = #{month}
and E.employee_no = #{empNo} ) where row_numbers between #{begin} and #{end}
